pairRData.map:
function(doc) {
  var re = new RegExp("^wim"); 
  if( ! re.test(doc._id) ){
    var re2 = new RegExp("^wim.*paired\.RData$"); 
    var keys = Object.keys(doc._attachments);
    var ys = keys.filter(function(k){return re2.test(k)});

    ys.forEach(function(y){
      var parts=y.split('.')
      emit([parts[5],[parts[0],parts[1],parts[2]].join('.'),y],null)
    })
  }
}
